function extractItems(xml) {
  const items = [];
  let start = 0;

  while (true) {
    const itemStart = xml.indexOf('<item>', start);
    if (itemStart === -1) break;
    const endItem = xml.indexOf('</item>', itemStart);
    if (endItem === -1) break;

    const itemXml = xml.substring(itemStart, endItem);

    const linkMatch = itemXml.match(/<link>([^<]+)<\/link>/);

    if (linkMatch) {
      items.push(linkMatch[1].trim());
    }

    start = endItem;
  }

  return items;
}

